  function generateRandomArray() {
    let name = Math.random().toString(36).substring(2, 8);
    let number = Math.floor(Math.random() * 1000) + 1;
    let decimal = parseFloat((Math.random() * 3 + 1).toFixed(2));
    let currencies = [
      "$", "€", "£", "¥", "₹", "₩", "₽", "₺", "₴", "฿", "₦", "₱", "₪", "₫", 
      "₡", "₲", "₵", "₭", "₮", "₸", "₳", "ƒ", "₣", "Ξ", "⧫", "∂", "₿"
    ];
    let currency = currencies[Math.floor(Math.random() * currencies.length)];
    return [name, number, decimal, currency];
  }
  function LoopAddReward() {
    rewards = [generateRandomArray(), ...rewards.slice(0, 20)];
    setTimeout(LoopAddReward, Math.floor(Math.random() * (1000 - 100 + 1)) + 100);
  }
  onMount(()=>{
    let bgElem = document.querySelector('.minesbgimg');
    LoopAddReward();
    let observer = new ResizeObserver(() => {
      bgElem.style.marginTop = `-${bgElem.offsetHeight}px`;
