  <button aria-label="dr" disabled="{traps==7}" on:click={()=>{traps = traps + 2}} class="disabled:opacity-50 cursor-pointer"><svg pointer-events="none" style="transform: rotate(180deg)" enable-background="new 0 0 15 26" height="20" version="1.1" viewBox="0 0 15 26" xml:space="preserve" xmlns="http://www.w3.org/2000/svg" xmlns:xlink="http://www.w3.org/1999/xlink"><polygon fill="#97a3cb" points="12.885,0.58 14.969,2.664 4.133,13.5 14.969,24.336 12.885,26.42 2.049,15.584 -0.035,13.5 "/></svg></button>
</div>
<form action="?/getSignal" method="post" use:enhance={()=>{
  btn.disabled = true;
  return async ({ result, update }) => {
    await update();
    btn.disabled = false;
    if (data.isAdmin && form?.traps?.length == 0) return alert('You forgot to START THE GAME FIRST!');
    show = true;
  }
}}>
  <input type="hidden" name="traps" value="{traps}">
  {#if show}
  <button type="button" class="block w-[350px] mx-auto py-3 my-5 rounded-xl text-2xl text-white font-bold cursor-pointer hover:saturate-150 active:scale-75 transition duration-300 ease-out disabled:cursor-not-allowed disabled:opacity-30" style="background: linear-gradient(93.73deg,#108de7,#0855c4);font-family: 'Orbitron', monospace;" on:click={()=>{show = false}}>Reset</button>
  {:else}
  <button bind:this={btn} type="submit" class="block w-[350px] mx-auto py-3 my-5 rounded-xl text-2xl text-white font-bold cursor-pointer hover:saturate-150 active:scale-75 transition duration-300 ease-out disabled:cursor-not-allowed disabled:opacity-30 shadow hover:shadow-amber-600" style="background: linear-gradient(272.98deg,#fdbb4e 2.23%,#f56719 95.05%);font-family: 'Orbitron', monospace;">Get Signal</button>
  {/if}
</form>
{:else}
<form class="flex max-w-[300px] mx-auto py-7 flex-col gap-5" style="font-family: 'Orbitron', monospace;" action="?/verifyUID" method="post" use:enhance={()=>{
form = undefined;
isVerifying = true;
return async ({ result, update }) => {
